import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  // Liste des images dans le dossier public
  const images = ["/image/1.png", "/image/2.png", "/image/3.png"];
  const [currentImage, setCurrentImage] = useState(images[0]); // Image initiale
  const [currentIndex, setCurrentIndex] = useState(0);

  // Changer les images toutes les 3 secondes
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => {
        const nextIndex = (prevIndex + 1) % images.length;
        setCurrentImage(images[nextIndex]);
        return nextIndex;
      });
    }, 3000); // Change toutes les 3 secondes

    return () => clearInterval(interval); // Nettoyer l'intervalle
  }, [images]);

  return (
    <div className="container">
      <header className="header">
        <h1>Bienvenue sur l'application de recyclage</h1>
        <p>
          Découvrez comment le recyclage peut aider à protéger notre planète en
          réduisant les déchets.
        </p>
      </header>

      {/* Section de l'image dynamique */}
      <div className="image-container">
        <img src={currentImage} alt="Recyclage dynamique" />
      </div>

      {/* Bouton vers la page d'upload */}
      <Link to="/upload">
        <button className="button">Aller à la classification d'image</button>
      </Link>
    </div>
  );
};

export default HomePage;
