import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  const [recycledItems, setRecycledItems] = useState(0);

  // Simule un compteur de matériaux recyclés
  useEffect(() => {
    const interval = setInterval(() => {
      setRecycledItems((prevCount) => prevCount + Math.floor(Math.random() * 5 + 1));
    }, 2000); // Augmente toutes les 2 secondes
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container">
      <header className="header">
        <h1>Bienvenue sur l'application de recyclage</h1>
        <p>
          Découvrez comment l'intelligence artificielle peut aider à trier vos déchets et
          à protéger notre planète.
        </p>
      </header>

      {/* Section éducative */}
      <section className="education-section">
        <h2>Types de matériaux recyclables</h2>
        <div className="recycling-types">
          <div className="type">
            <img src="/image/paper.png" alt="Papier" />
            <p>Papier</p>
          </div>
          <div className="type">
            <img src="/image/plastic.png" alt="Plastique" />
            <p>Plastique</p>
          </div>
          <div className="type">
            <img src="/image/glass.png" alt="Verre" />
            <p>Verre</p>
          </div>
          <div className="type">
            <img src="/image/compost.png" alt="Compost" />
            <p>Compost</p>
          </div>
        </div>
      </section>

      {/* Section des statistiques */}
      <section className="stats-section">
        <h2>Impact de notre communauté</h2>
        <p>Déchets recyclés grâce à notre application :</p>
        <h3>{recycledItems} objets recyclés</h3>
      </section>

      {/* Bouton vers la classification */}
      <Link to="/upload">
        <button className="button">Aller à la classification d'image</button>
      </Link>
    </div>
  );
};

export default HomePage;
