import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");

  const categories = [
    { name: "Cardboard", image: "/image/cardboard.png", description: "Recyclable cardboard materials." },
    { name: "Glass", image: "/image/glass.png", description: "Recyclable glass bottles and jars." },
    { name: "Metal", image: "/image/metal.png", description: "Aluminum and steel recyclable materials." },
    { name: "Paper", image: "/image/paper.png", description: "Recyclable paper and newspaper." },
    { name: "Plastic", image: "/image/plastic.png", description: "Recyclable plastic bottles and containers." },
    { name: "Trash", image: "/image/trash.png", description: "Non-recyclable waste materials." },
  ];

  // Filtrer les catégories selon la barre de recherche
  const filteredCategories = categories.filter((category) =>
    category.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="homepage">
      {/* Header Section */}
      <header className="main-header">
        <h1>Welcome to RecycleApp</h1>
        <p>Recycle smarter and contribute to a cleaner planet using our AI-powered tools.</p>
      </header>

      {/* Search Bar */}
      <div className="search-bar-container">
        <input
          type="text"
          placeholder="Search for a category..."
          className="search-bar"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Category Cards */}
      <section className="categories">
        <h2>Popular Categories</h2>
        <div className="card-container">
          {filteredCategories.length > 0 ? (
            filteredCategories.map((category, index) => (
              <div className="card" key={index}>
                <Link to={`/category/${category.name.toLowerCase()}`}>
                  <img src={category.image} alt={category.name} />
                  <div className="card-content">
                    <h3>{category.name}</h3>
                    <p>{category.description}</p>
                  </div>
                </Link>
              </div>
            ))
          ) : (
            <p className="no-results">No categories match your search.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
