import React from "react";
import { useParams } from "react-router-dom";
import "./CategoryPage.css";

const CategoryPage = () => {
  const { name } = useParams(); // Récupère le nom de la catégorie depuis l'URL

  // Données spécifiques pour chaque catégorie
  const categoryDetails = {
    cardboard: {
      title: "Cardboard",
      description:
        "Cardboard is a versatile and easily recyclable material. It can be turned into new boxes, packaging, or even paper products. Ensure to remove any non-recyclable materials like plastic tape or food residue before recycling.",
      benefits: "Recycling 1 ton of cardboard saves 17 trees and reduces waste in landfills by 30%.",
      tips: "Flatten cardboard boxes to save space in your recycling bin.",
      advice: "If you're unsure whether your cardboard is recyclable, use our AI-powered tool to classify it.",
    },
    glass: {
      title: "Glass",
      description:
        "Glass is one of the few materials that can be recycled infinitely without losing quality. Clean glass bottles and jars thoroughly before recycling them to avoid contamination.",
      benefits: "Recycling glass saves 30% of the energy needed to produce new glass and reduces greenhouse gas emissions.",
      tips: "Separate colored glass from clear glass if required by your local recycling program.",
      advice: "When in doubt about the recyclability of a glass item, let RecycleApp guide you!",
    },
    metal: {
      title: "Metal",
      description:
        "Recyclable metals include aluminum and steel. Aluminum cans, for example, can be melted and reused repeatedly. Cleaning them before recycling is essential to ensure proper processing.",
      benefits: "Recycling metal saves up to 95% of the energy required for new production and reduces the need for mining.",
      tips: "Crush aluminum cans to save space in your recycling bin.",
      advice: "If you're unsure whether your metal is recyclable, upload its photo on RecycleApp to find out.",
    },
    paper: {
      title: "Paper",
      description:
        "Paper is a highly recyclable material. Newspapers, office paper, and cardboard can all be recycled, but avoid recycling items contaminated with grease or food.",
      benefits: "Recycling paper saves energy, water, and reduces air pollution. One ton of recycled paper saves about 17 trees.",
      tips: "Remove any staples or paper clips before recycling paper.",
      advice: "Our app can help identify if a paper item is suitable for recycling. Give it a try!",
    },
    plastic: {
      title: "Plastic",
      description:
        "Plastic recycling can vary depending on the type of plastic. Check the recycling symbol and number on the plastic to determine its recyclability. Clean the plastic thoroughly before recycling.",
      benefits: "Recycling plastic reduces oil consumption by 40% and prevents ocean pollution.",
      tips: "Focus on recycling PET (#1) and HDPE (#2) plastics, which are widely accepted.",
      advice: "Not sure if your plastic is recyclable? Use RecycleApp for an accurate classification!",
    },
    trash: {
      title: "Trash",
      description:
        "Non-recyclable waste includes items contaminated with food, certain types of plastic, or hazardous materials. Proper disposal minimizes environmental harm.",
      benefits: "Disposing of trash properly prevents pollution and protects natural habitats.",
      tips: "Separate recyclables from trash to make the recycling process easier.",
      advice: "Upload an image of an item on RecycleApp to confirm if it's recyclable or should be disposed of.",
    },
  };

  const category = categoryDetails[name.toLowerCase()];

  if (!category) {
    return <h2>Category not found!</h2>;
  }

  return (
    <div className="category-page">
      <h1>{category.title}</h1>
      <p>{category.description}</p>
      <h3>Environmental Benefits</h3>
      <p>{category.benefits}</p>
      <h3>Recycling Tips</h3>
      <p>{category.tips}</p>
      <h3>Need Help?</h3>
      <p>
        {category.advice} With our AI-powered tool, you'll never have to guess
        again!
      </p>
    </div>
  );
};

export default CategoryPage;
