import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  const images = ["/image/1.png", "/image/2.png", "/image/3.png"];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Gestion du slider d'images
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change toutes les 3 secondes
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="container">
      {/* Titre principal */}
      <header className="header">
        <h1>Bienvenue sur l'application de recyclage</h1>
        <p>
          Découvrez comment l'intelligence artificielle peut vous aider à trier
          vos déchets efficacement.
        </p>
      </header>

      {/* Slider d'images */}
      <div className="slider">
        <img
          src={images[currentImageIndex]}
          alt="Recyclage"
          className="slider-image"
        />
      </div>

      {/* Section éducative */}
      <section className="education-section">
        <h2>Types de matériaux recyclables</h2>
        <div className="recycling-types">
          <div className="type">
            <img src="/image/paper.png" alt="Papier" />
            <p>Papier</p>
          </div>
          <div className="type">
            <img src="/image/plastic.png" alt="Plastique" />
            <p>Plastique</p>
          </div>
          <div className="type">
            <img src="/image/glass.png" alt="Verre" />
            <p>Verre</p>
          </div>
          <div className="type">
            <img src="/image/compost.png" alt="Compost" />
            <p>Compost</p>
          </div>
        </div>
      </section>

      {/* Bouton pour aller à la classification */}
      <Link to="/upload">
        <button className="button">Aller à la classification d'image</button>
      </Link>
    </div>
  );
};

export default HomePage;
