import React from "react";
import { useParams } from "react-router-dom";
import "./CategoryPage.css";

const CategoryPage = () => {
  const { name } = useParams(); // Récupère le nom de la catégorie depuis l'URL

  // Données sur chaque catégorie
  const categoryDetails = {
    cardboard: {
      title: "Cardboard",
      description:
        "Cardboard is one of the easiest materials to recycle. Break it down into flat pieces and place it in your recycling bin.",
      impact: "Recycling 1 ton of cardboard saves 9 cubic yards of landfill space.",
      percentage: "Recycling cardboard reduces landfill waste by 30%.",
    },
    glass: {
      title: "Glass",
      description:
        "Glass can be recycled endlessly without loss of quality. Rinse your glass bottles and jars before recycling them.",
      impact: "Recycling glass saves 30% of the energy required to make new glass.",
      percentage: "Recycling glass reduces pollution by 20%.",
    },
    metal: {
      title: "Metal",
      description:
        "Metals like aluminum and steel can be recycled repeatedly without degradation. Clean and separate your metals before recycling.",
      impact: "Recycling metal saves 95% of the energy needed to produce new metal.",
      percentage: "Recycling metal helps reduce greenhouse gas emissions by 40%.",
    },
    paper: {
      title: "Paper",
      description:
        "Paper is highly recyclable. Remove staples and paper clips before placing it in the recycling bin.",
      impact: "Recycling paper saves 17 trees per ton.",
      percentage: "Recycling paper reduces water pollution by 35%.",
    },
    plastic: {
      title: "Plastic",
      description:
        "Plastics can be recycled but must be sorted correctly. Look for the recycling symbol on your plastic products.",
      impact: "Recycling plastic reduces oil consumption by 40%.",
      percentage: "Recycling plastic reduces ocean pollution by 25%.",
    },
    trash: {
      title: "Trash",
      description:
        "Non-recyclable trash should be disposed of properly to avoid environmental harm. Consider alternatives to reduce waste.",
      impact: "Proper waste disposal prevents harmful chemicals from polluting the environment.",
      percentage: "Reducing trash can lower landfill usage by 20%.",
    },
  };

  const category = categoryDetails[name];

  if (!category) {
    return <h2>Category not found!</h2>; // Si le nom de la catégorie est invalide
  }

  return (
    <div className="category-page">
      <h1>{category.title}</h1>
      <p>{category.description}</p>
      <h3>Environmental Impact</h3>
      <p>{category.impact}</p>
      <h3>How Much We Save</h3>
      <p>{category.percentage}</p>
    </div>
  );
};

export default CategoryPage;
