import React from "react";
import { useParams } from "react-router-dom";
import "./CategoryPage.css";

const CategoryPage = () => {
  const { name } = useParams(); // Récupère le nom de la catégorie depuis l'URL

  // Données spécifiques pour chaque catégorie
  const categoryDetails = {
    cardboard: {
      title: "Cardboard",
      description: "Recyclable cardboard materials can be turned into new boxes or paper products.",
      benefits: "Recycling cardboard saves trees and reduces waste in landfills.",
    },
    glass: {
      title: "Glass",
      description: "Recyclable glass can be melted and reshaped indefinitely without loss of quality.",
      benefits: "Recycling glass saves energy and reduces greenhouse gas emissions.",
    },
    metal: {
      title: "Metal",
      description: "Recyclable metals include aluminum and steel, which can be reused repeatedly.",
      benefits: "Recycling metal reduces mining and saves significant energy.",
    },
    paper: {
      title: "Paper",
      description: "Recyclable paper can be turned into new sheets or cardboard products.",
      benefits: "Recycling paper saves trees and reduces water and air pollution.",
    },
    plastic: {
      title: "Plastic",
      description: "Recyclable plastics can be repurposed into a variety of new products.",
      benefits: "Recycling plastic reduces oil usage and prevents ocean pollution.",
    },
    trash: {
      title: "Trash",
      description: "Non-recyclable waste should be disposed of properly to minimize environmental harm.",
      benefits: "Proper disposal prevents toxins from polluting the environment.",
    },
  };

  const category = categoryDetails[name.toLowerCase()];

  if (!category) {
    return <h2>Category not found!</h2>;
  }

  return (
    <div className="category-page">
      <h1>{category.title}</h1>
      <p>{category.description}</p>
      <h3>Environmental Benefits</h3>
      <p>{category.benefits}</p>
    </div>
  );
};

export default CategoryPage;
