import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";
import { siteData } from "../data";

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");

  // Rechercher dans toutes les données du site
  const filteredResults = {
    categories: siteData.categories.filter((category) =>
      category.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      category.description.toLowerCase().includes(searchTerm.toLowerCase())
    ),
    sections: siteData.sections.filter((section) =>
      section.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      section.content.toLowerCase().includes(searchTerm.toLowerCase())
    ),
  };

  return (
    <div className="homepage">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="logo-container">
          <img src="/image/logo.png" alt="RecycleApp Logo" className="logo" />
        </div>
        <input
          type="text"
          placeholder="Search for recyclable items or tips..."
          className="search-bar"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="nav-links">
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/upload">Classify Image</Link>
        </div>
      </nav>

      {/* Main Header */}
      <header className="main-header">
        <div className="main-text">
          <h1>Recycle the Unrecyclable with RecycleApp</h1>
          <p>
            Use our AI-powered application to classify waste and contribute to
            a greener planet.
          </p>
        </div>
        <div className="main-image-container">
          <img
            src="/image/main-header.png"
            alt="Recycling"
            className="main-image"
          />
        </div>
      </header>

      {/* Search Results */}
      <section className="search-results">
        <h2>Search Results</h2>
        {filteredResults.categories.length > 0 && (
          <div className="categories-results">
            <h3>Categories:</h3>
            {filteredResults.categories.map((category) => (
              <div key={category.name} className="search-item">
                <Link to={`/category/${category.name.toLowerCase()}`}>
                  <strong>{category.name}</strong>: {category.description}
                </Link>
              </div>
            ))}
          </div>
        )}
        {filteredResults.sections.length > 0 && (
          <div className="sections-results">
            <h3>Sections:</h3>
            {filteredResults.sections.map((section, index) => (
              <div key={index} className="search-item">
                <strong>{section.title}</strong>: {section.content}
              </div>
            ))}
          </div>
        )}
        {filteredResults.categories.length === 0 && filteredResults.sections.length === 0 && (
          <p className="no-results">No results match your search.</p>
        )}
      </section>
    </div>
  );
};

export default HomePage;
